#include <iostream>
#include <algorithm>

using namespace std;

int main() {
  long long w, a, b;
  cin >> w >> a >> b;

  long long ans = 0;
  if (a + w >= b && b + w >= a) {
    ans = 0;
  } else if (b > a + w) {
    ans = b - (a + w);
  } else {
    ans = a - (b + w);
  }
  cout << max(0LL, ans) << endl;
  return 0;
}
