#include <stdio.h>
#include <stdlib.h>
#include <math.h>

int main() {
    long long x;
    scanf("%lld", &x);
    if (x == 0) {
        printf("0\n");
        return 0;
    }
    long long left = 1, right = 2000000;
    long long ans = -1;
    while (left <= right) {
        long long mid = left + (right - left) / 2;
        long long sum = mid * (mid + 1) / 2;
        if (sum >= abs(x)) {
            ans = mid;
            right = mid - 1;
        } else {
            left = mid + 1;
        }
    }
    if (ans % 2 == 0 && x > 0) ans++;
    else if(ans % 2 != 0 && x < 0) ans++;

    printf("%lld\n", ans);
    return 0;
}
