#include <iostream>
#include <cmath>

using namespace std;

int main() {
  long long x;
  cin >> x;

  if (x == 0) {
    cout << 0 << endl;
    return 0;
  }

  long long time = 0;
  long long current_pos = 0;
  long long i = 1;

  while (current_pos != x) {
    long long diff = abs(x - current_pos);
    if (diff == 0) break;
    if (diff <= i) {
      time +=1;
      current_pos = x;
    } else {
      long long next_pos;
      if (x > current_pos) {
          next_pos = current_pos + i;
      } else {
          next_pos = current_pos - i;
      }
      current_pos = next_pos;
      time += 1;

    }
    i++;
  }
  cout << time << endl;
  return 0;
}
