import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long x = sc.nextLong();
        long l = 0, r = 200000;
        long ans = -1;
        while (l <= r) {
            long mid = (l + r) / 2;
            long sum = 0;
            for (long i = 1; i <= mid; i++) {
                sum += i;
            }
            if (sum >= x) {
                ans = mid;
                r = mid - 1;
            } else {
                l = mid + 1;
            }
        }
        if (ans == -1) {
            System.out.println(x);
        } else {
            long cur = 0;
            long time = 0;
            for (long i = ans; i >= 1; i--) {
                if (cur + i <= x) {
                    cur += i;
                    time++;
                } else if (cur - i >= 0) {
                    cur -= i;
                    time++;
                } else {
                    time++;
                }

                if(cur == x){
                    break;
                }
            }
            if(cur != x) {
                System.out.println(x);
            } else {
                System.out.println(ans);
            }
        }

    }
}
