#include <iostream>
#include <vector>
#include <numeric>

using namespace std;

int main() {
    int n, k;
    cin >> n >> k;

    vector<long long> a(n);
    for (int i = 0; i < n; ++i) {
        cin >> a[i];
    }

    int unnecessary_count = 0;
    for (int i = 0; i < n; ++i) {
        bool unnecessary = true;
        for (int j = 0; j < (1 << n); ++j) {
            if ((j >> i) & 1) { 
                vector<long long> subset;
                long long sum = 0;
                for (int bit = 0; bit < n; ++bit) {
                    if ((j >> bit) & 1) {
                        subset.push_back(a[bit]);
                        sum += a[bit];
                    }
                }

                if (sum >= k) {
                    long long sum_without_i = sum - a[i];
                    bool good_without_i = false;
                    if (subset.size() >1){
                        for (int l = 0; l < (1 << (n-1)); ++l){
                            long long cur_sum = 0;
                            vector<long long> subset_without_i;
                            int cur_index = 0;
                            for (int bit = 0; bit < n; ++bit){
                                if (bit != i){
                                    if((l >> cur_index) & 1){
                                        cur_sum += a[bit];
                                        subset_without_i.push_back(a[bit]);
                                    }
                                    cur_index++;
                                }
                            }
                            if (cur_sum >=k && subset_without_i.size() == subset.size()-1 && cur_sum == sum_without_i) good_without_i = true;
                        }
                    }

                    if (!good_without_i) {
                        unnecessary = false;
                        break;
                    }
                }
            }
        }
        if (unnecessary) {
            unnecessary_count++;
        }
    }

    cout << unnecessary_count << endl;

    return 0;
}
