def solve():
    n, k = map(int, input().split())
    a = list(map(int, input().split()))

    count = 0
    for i in range(n):
        is_unnecessary = True
        for j in range(1 << n):
            subset = []
            for bit in range(n):
                if (j >> bit) & 1:
                    subset.append(a[bit])

            if i in [idx for idx,val in enumerate(a) if val in subset] and sum(subset) >= k:
                subset_without_i = [x for idx, x in enumerate(subset) if idx != [idx for idx,val in enumerate(a) if val == a[i]][0]]
                if sum(subset_without_i) >= k:
                    continue
                else:
                    is_unnecessary = False
                    break
        if is_unnecessary:
            count += 1
    print(count)

solve()
