#include <stdio.h>
#include <stdlib.h>

int main() {
    long long x;
    scanf("%lld", &x);
    long long l = 0, r = 2000000;
    long long ans = r;
    while (l <= r) {
        long long mid = (l + r) / 2;
        long long sum = 0;
        long long cur = 0;
        for (long long i = mid; i >= 1; i--) {
            long long diff = cur - x;
            if (diff == 0) {
                sum = mid;
                break;
            }
            if (diff > 0) {
                if (diff >= i) {
                    cur -= i;
                } else {
                    cur = x;
                    sum = mid;
                    break;
                }
            } else {
                if (-diff >= i) {
                    cur += i;
                } else {
                    cur = x;
                    sum = mid;
                    break;
                }
            }

        }
        if (cur == x) {
            ans = mid;
            r = mid - 1;
        } else {
            l = mid + 1;
        }
    }
    printf("%lld\n", ans);
    return 0;
}
