#include <iostream>
#include <cmath>

using namespace std;

int main() {
  long long x;
  cin >> x;

  if (x == 0) {
    cout << 0 << endl;
    return 0;
  }

  long long l = 0, r = 200000;
  long long ans = -1;

  while (l <= r) {
    long long mid = l + (r - l) / 2;
    long long sum = 0;
    for (long long i = 1; i <= mid; ++i) {
      sum += i;
    }

    if (abs(x) <= sum) {
      ans = mid;
      r = mid - 1;
    } else {
      l = mid + 1;
    }
  }
  cout << ans << endl;
  return 0;
}
