import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        long x = scanner.nextLong();
        long time = 0;
        long pos = 0;
        long i = 1;
        while (pos != x) {
            if (pos < x) {
                if (pos + i <= x) {
                    pos += i;
                } else {
                    pos = x;
                }
            } else {
                if (pos - i >= x) {
                    pos -= i;
                } else {
                    pos = x;
                }
            }
            i++;
            time++;

        }
        System.out.println(time);
        scanner.close();

    }
}
