#include <stdio.h>
#include <stdlib.h>

int main() {
    int N, K;
    scanf("%d %d", &N, &K);
    long long a[N];
    for (int i = 0; i < N; i++) {
        scanf("%lld", &a[i]);
    }
    int count = 0;
    for (int i = 0; i < N; i++) {
        int unnecessary = 1;
        for (int j = 0; j < (1 << N); j++) {
            if ((j >> i) & 1) {
                long long sum = 0;
                for (int k = 0; k < N; k++) {
                    if ((j >> k) & 1) {
                        sum += a[k];
                    }
                }
                if (sum >= K) {
                    long long sum2 = 0;
                    for (int k = 0; k < N; k++) {
                        if ((j >> k) & 1 && k != i) {
                            sum2 += a[k];
                        }
                    }
                    if (sum2 < K) {
                        unnecessary = 0;
                        break;
                    }
                }
            }
        }
        count += unnecessary;
    }
    printf("%d\n", count);
    return 0;
}
