import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int k = sc.nextInt();
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt();
        }
        int count = 0;
        for (int i = 0; i < n; i++) {
            boolean unnecessary = true;
            for (int j = 0; j < (1 << n); j++) {
                if ((j >> i & 1) == 0) continue;
                long sum = 0;
                for (int l = 0; l < n; l++) {
                    if ((j >> l & 1) == 1) {
                        sum += a[l];
                    }
                }
                if (sum >= k) {
                    long sum2 = 0;
                    for (int l = 0; l < n; l++) {
                        if ((j >> l & 1) == 1 && l != i) {
                            sum2 += a[l];
                        }
                    }
                    if (sum2 < k) {
                        unnecessary = false;
                        break;
                    }
                }
            }
            if (unnecessary) count++;
        }
        System.out.println(count);
    }
}
