#include <stdio.h>
#include <stdlib.h>
#include <limits.h>

long long min(long long a, long long b) {
    return a < b ? a : b;
}

int main() {
    int n;
    scanf("%d", &n);
    long long l[n], r[n];
    for (int i = 0; i < n; i++) {
        scanf("%lld %lld", &l[i], &r[i]);
    }
    long long dp[n][n];
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            dp[i][j] = LLONG_MAX;
        }
    }
    for (int i = 0; i < n; i++) {
        dp[i][i] = 0;
    }
    for (int len = 2; len <= n; len++) {
        for (int i = 0; i <= n - len; i++) {
            int j = i + len - 1;
            for (int k = i; k < j; k++) {
                long long cost = dp[i][k] + dp[k + 1][j];
                long long leftmost = l[i];
                long long rightmost = r[j];
                for (int m = i; m <= k; m++) {
                    leftmost = min(leftmost, l[m]);
                    rightmost = max(rightmost, r[m]);
                }
                for (int m = k + 1; m <= j; m++) {
                    leftmost = min(leftmost, l[m]);
                    rightmost = max(rightmost, r[m]);
                }
                long long moveCost = 0;
                if (rightmost > leftmost)
                    moveCost = 0;
                else
                  moveCost = rightmost - leftmost;


                for (int m = i; m <=j; m++) {
                    moveCost += abs(l[m] - leftmost);
                }
                dp[i][j] = min(dp[i][j], cost + moveCost);
            }
        }
    }
    printf("%lld\n", dp[0][n - 1]);
    return 0;
}
