#include <stdio.h>
#include <string.h>

int main() {
    int H, W;
    scanf("%d %d", &H, &W);
    char S[H][W + 1];
    for (int i = 0; i < H; i++) {
        scanf("%s", S[i]);
    }
    char T[H][W + 1];
    for (int i = 0; i < H; i++) {
        strcpy(T[i], S[i]);
    }
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            if (S[i][j] == '.') {
                int count = 0;
                for (int x = -1; x <= 1; x++) {
                    for (int y = -1; y <= 1; y++) {
                        if (x == 0 && y == 0) continue;
                        int ni = i + x;
                        int nj = j + y;
                        if (ni >= 0 && ni < H && nj >= 0 && nj < W && S[ni][nj] == '#') {
                            count++;
                        }
                    }
                }
                T[i][j] = count + '0';
            }
        }
    }
    for (int i = 0; i < H; i++) {
        printf("%s\n", T[i]);
    }
    return 0;
}
