import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[][] rect = new long[n][2];
        for (int i = 0; i < n; i++) {
            rect[i][0] = sc.nextLong();
            rect[i][1] = sc.nextLong();
        }
        long minCost = Long.MAX_VALUE;
        for (int i = 0; i < n; i++) {
            long cost = 0;
            long[] pos = new long[n];
            pos[0] = rect[i][0];
            for (int j = 1; j < n; j++) {
                long cur = rect[j][0];
                long prev = rect[j-1][1];
                if(cur>prev){
                    pos[j] = prev;
                    cost += cur - prev;
                }else{
                    pos[j] = cur;
                }

            }
            for(int j = n-2;j>=0;j--){
                long cur = rect[j][1];
                long next = rect[j+1][0];
                if(cur<next){
                    long diff = next - cur;
                    cost += diff;
                    for(int k=0;k<=j;k++) pos[k] += diff;

                }
            }
            minCost = Math.min(minCost, cost);

        }
        System.out.println(minCost);
    }
}
