#include <stdio.h>
#include <stdlib.h>

int main() {
    int A, B;
    scanf("%d %d", &A, &B);
    if (A <= B) {
        printf("Impossible\n");
        return 0;
    }
    int N = A + B;
    char ans[N][N];
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < N; j++) {
            printf("? %d %d\n", i, j);
            fflush(stdout);
            char c;
            scanf(" %c", &c);
            ans[i][j] = c;
        }
    }
    int honest[N];
    for (int i = 0; i < N; i++) honest[i] = 1;
    for (int i = 0; i < N; i++) {
        int count = 0;
        for (int j = 0; j < N; j++) {
            if (ans[i][j] == 'Y') count++;
        }
        if (count < A) honest[i] = 0;
    }
    int count_honest = 0;
    for (int i = 0; i < N; i++) if (honest[i] == 1) count_honest++;
    if (count_honest != A) {
        printf("Impossible\n");
        return 0;
    }
    printf("!");
    for (int i = 0; i < N; i++) printf("%d", honest[i]);
    printf("\n");
    return 0;
}
