import java.io.*;
import java.util.*;

public class Main {
    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        BufferedWriter bw = new BufferedWriter(new OutputStreamWriter(System.out));
        String[] input = br.readLine().split(" ");
        int A = Integer.parseInt(input[0]);
        int B = Integer.parseInt(input[1]);
        int N = A + B;
        if (B > A) {
            bw.write("Impossible");
            bw.flush();
            return;
        }
        boolean[][] honest = new boolean[N][N];
        for (int i = 0; i < N; i++) {
            for (int j = 0; j < N; j++) {
                bw.write("? " + i + " " + j + "\n");
                bw.flush();
                String ans = br.readLine();
                honest[i][j] = ans.equals("Y");
            }
        }
        int[] result = new int[N];
        for (int i = 0; i < (1 << N); i++) {
            int count = 0;
            for (int j = 0; j < N; j++) {
                if ((i >> j) % 2 == 1) count++;
            }
            if (count != A) continue;
            boolean possible = true;
            for (int j = 0; j < N; j++) {
                for (int k = 0; k < N; k++) {
                    boolean expected = (i >> k) % 2 == 1;
                    if (((i >> j) % 2 == 1) != honest[j][k] && expected != honest[j][k]) {
                        possible = false;
                        break;
                    }
                }
                if (!possible) break;
            }
            if (possible) {
                for (int j = 0; j < N; j++) {
                    result[j] = (i >> j) % 2;
                }
                break;
            }
        }
        bw.write("!");
        for (int i = 0; i < N; i++) {
            bw.write(result[i]);
        }
        bw.write("\n");
        bw.flush();
    }
}
