A, B = map(int, input().split())
N = A + B
if B > A:
    print("Impossible")
else:
    honest = [0] * N
    for i in range(N):
        for j in range(N):
            if i != j:
                print(f'? {i} {j}')
                ans = input()
                if ans == 'Y':
                    honest[j] +=1

    honest_indices = sorted(range(len(honest)), key=lambda i: honest[i], reverse = True)
    result = [0] * N
    for i in range(A):
        result[honest_indices[i]] = 1
    print('!' + ''.join(map(str, result)))

