#include <stdio.h>
#include <stdlib.h>

int cmpfunc(const void * a, const void * b) {
   return *(int*)a - *(int*)b;
}

int main() {
    long long n, c, k;
    scanf("%lld %lld %lld", &n, &c, &k);
    long long t[n];
    for (long long i = 0; i < n; i++) {
        scanf("%lld", &t[i]);
    }
    qsort(t, n, sizeof(long long), cmpfunc);
    long long buses = 0;
    long long i = 0;
    while (i < n) {
        buses++;
        long long count = 0;
        long long j = i;
        long long departure_time = t[i] + k;
        while (j < n && t[j] <= departure_time && count < c) {
            count++;
            j++;
        }
        i = j;
    }
    printf("%lld\n", buses);
    return 0;
}
