#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  long long n, c, k;
  cin >> n >> c >> k;

  vector<long long> t(n);
  for (int i = 0; i < n; ++i) {
    cin >> t[i];
  }
  sort(t.begin(), t.end());

  long long buses = 0;
  long long i = 0;
  while (i < n) {
    buses++;
    long long count = 0;
    long long departure_time = t[i] + k;
    while (i < n && t[i] <= departure_time && count < c) {
      count++;
      i++;
    }
  }
  cout << buses << endl;
  return 0;
}
