#include <stdio.h>
#include <stdlib.h>

typedef struct {
    long long size;
    int color;
} Creature;

int can_absorb(Creature a, Creature b) {
    return b.size <= 2 * a.size;
}

int main() {
    int n;
    scanf("%d", &n);

    Creature *creatures = malloc(n * sizeof(Creature));
    for (int i = 0; i < n; i++) {
        creatures[i].color = i + 1;
        scanf("%lld", &creatures[i].size);
    }

    int possible_colors[n + 1];
    for (int i = 0; i <= n; i++) possible_colors[i] = 0;


    int count = n;
    while (count > 1) {
        int absorbed = 0;
        for (int i = 0; i < count; i++) {
            for (int j = 0; j < count; j++) {
                if (i == j) continue;
                if (can_absorb(creatures[i], creatures[j])) {
                    creatures[i].size += creatures[j].size;
                    creatures[j].size = -1; 
                    absorbed = 1;
                    break;
                }
            }
        }

        int new_count = 0;
        for (int i = 0; i < count; i++) {
            if (creatures[i].size != -1) {
                creatures[new_count] = creatures[i];
                new_count++;
            }
        }
        count = new_count;
    }

    printf("%d\n", creatures[0].color);

    free(creatures);
    return 0;
}
