#include <iostream>
#include <vector>

using namespace std;

int main() {
    int h, w;
    cin >> h >> w;

    vector<string> s(h);
    for (int i = 0; i < h; ++i) {
        cin >> s[i];
    }

    vector<string> result = s;
    for (int i = 0; i < h; ++i) {
        for (int j = 0; j < w; ++j) {
            if (s[i][j] == '.') {
                int count = 0;
                for (int x = -1; x <= 1; ++x) {
                    for (int y = -1; y <= 1; ++y) {
                        if (x == 0 && y == 0) continue;
                        int ni = i + x;
                        int nj = j + y;
                        if (ni >= 0 && ni < h && nj >= 0 && nj < w && s[ni][nj] == '#') {
                            count++;
                        }
                    }
                }
                result[i][j] = count + '0';
            }
        }
    }

    for (int i = 0; i < h; ++i) {
        cout << result[i] << endl;
    }

    return 0;
}
