#include <iostream>
#include <vector>
#include <set>

using namespace std;

struct Creature {
    long long size;
    int color;
};

int main() {
    int n;
    cin >> n;

    vector<Creature> creatures(n);
    for (int i = 0; i < n; ++i) {
        creatures[i].size = 0;
        creatures[i].color = i + 1;
        cin >> creatures[i].size;
    }

    set<int> possibleColors;
    vector<bool> merged(n, false);

    function<void(vector<Creature>&)> mergeCreatures = [&](vector<Creature>& currentCreatures) {
        if (currentCreatures.size() == 1) {
            possibleColors.insert(currentCreatures[0].color);
            return;
        }

        bool mergedThisRound = false;
        for (int i = 0; i < currentCreatures.size(); ++i) {
            if (merged[i]) continue;
            for (int j = 0; j < currentCreatures.size(); ++j) {
                if (i == j || merged[j]) continue;
                if (currentCreatures[j].size <= 2 * currentCreatures[i].size) {

                    vector<Creature> nextCreatures;
                    Creature mergedCreature;
                    mergedCreature.size = currentCreatures[i].size + currentCreatures[j].size;
                    mergedCreature.color = currentCreatures[i].color;

                    for(int k=0; k < currentCreatures.size(); ++k){
                        if(k != i && k != j){
                            nextCreatures.push_back(currentCreatures[k]);
                        }
                    }
                    nextCreatures.push_back(mergedCreature);

                    merged[i] = true;
                    merged[j] = true;
                    mergeCreatures(nextCreatures);
                    mergedThisRound = true;
                    return;

                } else if (currentCreatures[i].size <= 2 * currentCreatures[j].size) {
                    vector<Creature> nextCreatures;
                    Creature mergedCreature;
                    mergedCreature.size = currentCreatures[j].size + currentCreatures[i].size;
                    mergedCreature.color = currentCreatures[j].color;
                    for(int k=0; k < currentCreatures.size(); ++k){
                        if(k != i && k != j){
                            nextCreatures.push_back(currentCreatures[k]);
                        }
                    }
                    nextCreatures.push_back(mergedCreature);
                    merged[i] = true;
                    merged[j] = true;
                    mergeCreatures(nextCreatures);
                    mergedThisRound = true;
                    return;
                }
            }
        }
    };

    mergeCreatures(creatures);
    cout << possibleColors.size() << endl;

    return 0;
}
