import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        List<Pair> creatures = new ArrayList<>();
        for (int i = 1; i <= n; i++) {
            creatures.add(new Pair(sc.nextInt(), i));
        }
        Collections.sort(creatures, Comparator.comparingInt(p -> p.size));
        Set<Integer> colors = new HashSet<>();
        colors.add(creatures.get(0).color);
        for (int i = 1; i < n; i++) {
            boolean merged = false;
            for (int j = 0; j < i; j++) {
                if (creatures.get(i).size <= 2 * creatures.get(j).size) {
                    creatures.get(j).size += creatures.get(i).size;
                    merged = true;
                    break;
                }
            }
            if (!merged) {
                colors.add(creatures.get(i).color);
            }
        }
        System.out.println(colors.size());
    }

    static class Pair {
        int size;
        int color;

        Pair(int size, int color) {
            this.size = size;
            this.color = color;
        }
    }
}
