def solve():
    n = int(input())
    a = list(map(int, input().split()))

    creatures = []
    for i in range(n):
        creatures.append((a[i], i + 1))

    while len(creatures) > 1:
        creatures.sort()

        merged = False
        for i in range(len(creatures)):
            for j in range(len(creatures)):
                if i == j:
                    continue

                if creatures[j][0] <= 2 * creatures[i][0]:
                    new_size = creatures[i][0] + creatures[j][0]
                    new_color = creatures[i][1]
                    creatures.pop(j)
                    creatures[i] = (new_size, new_color)
                    merged = True
                    break
            if merged:
                break

    print(creatures[0][1])

solve()
