import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        List<Integer>[] graph = new ArrayList[n + 1];
        for (int i = 1; i <= n; i++) {
            graph[i] = new ArrayList<>();
        }
        for (int i = 0; i < m; i++) {
            int u = sc.nextInt();
            int v = sc.nextInt();
            graph[u].add(v);
            graph[v].add(u);
        }
        int[][] newGraph = new int[n * n + 1][n * n + 1];
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= n; j++) {
                for (int k = 1; k <= n; k++) {
                    for (int l = 1; l <= n; l++) {
                        boolean edge1 = false;
                        boolean edge2 = false;
                        for (int nei : graph[i]) {
                            if (nei == k) {
                                edge1 = true;
                                break;
                            }
                        }
                        for (int nei : graph[j]) {
                            if (nei == l) {
                                edge2 = true;
                                break;
                            }
                        }
                        if (edge1 && edge2) {
                            newGraph[(i - 1) * n + j][(k - 1) * n + l] = 1;
                            newGraph[(k - 1) * n + l][(i - 1) * n + j] = 1;
                        }
                    }
                }
            }
        }
        boolean[] visited = new boolean[n * n + 1];
        int count = 0;
        for (int i = 1; i <= n * n; i++) {
            if (!visited[i]) {
                count++;
                dfs(newGraph, visited, i);
            }
        }
        System.out.println(count);
        sc.close();
    }
    public static void dfs(int[][] graph, boolean[] visited, int node) {
        visited[node] = true;
        for (int i = 1; i < graph.length; i++) {
            if (graph[node][i] == 1 && !visited[i]) {
                dfs(graph, visited, i);
            }
        }
    }
}
