#include <stdio.h>
#include <string.h>

int main() {
    long long n, k;
    char s[200001];
    scanf("%lld %lld", &n, &k);
    scanf("%s", s);
    for (long long i = 0; i < k; i++) {
        long long cur = 0;
        char state = s[cur];
        while (cur >= 0 && cur < n) {
            if (state == 'A') {
                s[cur] = 'B';
                cur++;
            } else {
                s[cur] = 'A';
                cur--;
            }
            if (cur >= 0 && cur < n)
                state = s[cur];
        }
    }
    printf("%s\n", s);
    return 0;
}
