#include <iostream>
#include <string>
#include <vector>

using namespace std;

int main() {
  long long n, k;
  string s;
  cin >> n >> k >> s;

  vector<char> state(n);
  for (int i = 0; i < n; ++i) {
    state[i] = s[i];
  }

  for (long long i = 0; i < k; ++i) {
    long long cur = 0;
    bool left = true;
    while (cur < n) {
      if (state[cur] == 'A') {
        if (left) {
          state[cur] = 'B';

        } else {
          state[cur] = 'B';

        }
        if(left) cur++; else cur--;

      } else {
        if (left) {
          state[cur] = 'A';
          cur--;
          left = false;
        } else {
          state[cur] = 'A';
          cur++;
          left = true;
        }
      }
    }
  }

  for (int i = 0; i < n; ++i) {
    cout << state[i];
  }
  cout << endl;
  return 0;
}
