import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long k = sc.nextLong();
        String s = sc.next();
        char[] a = s.toCharArray();
        for (long i = 0; i < k; i++) {
            int cur = 0;
            char state = a[cur];
            while (true) {
                if (state == 'A') {
                    a[cur] = 'B';
                    if (cur == n - 1) break;
                    cur++;
                    state = a[cur];
                } else {
                    if (cur == 0) {
                        a[cur] = 'A';
                        break;
                    } else {
                        a[cur] = 'A';
                        cur--;
                        state = a[cur];
                    }
                }
            }
        }
        System.out.println(String.valueOf(a));
    }
}
