#include <stdio.h>
#include <string.h>
#include <stdlib.h>

int isIncreasing(char *s) {
    int len = strlen(s);
    for (int i = 0; i < len - 1; i++) {
        if (s[i] > s[i + 1]) return 0;
    }
    return 1;
}

int main() {
    char n[500001];
    scanf("%s", n);
    int len = strlen(n);
    int count = 0;
    while (len > 0) {
        char maxDigit = '0';
        int maxIndex = -1;
        for (int i = 0; i < len; i++) {
            if (n[i] >= maxDigit) {
                maxDigit = n[i];
                maxIndex = i;
            }
        }
        if (maxIndex == 0) {
            count++;
            break;
        }
        char num[maxIndex + 1];
        strncpy(num, n, maxIndex);
        num[maxIndex] = '\0';
        if (isIncreasing(num)) {
            count++;
        }
        else
        {
            count++;
            char temp[maxIndex+1];
            strncpy(temp, n, maxIndex);
            temp[maxIndex] = '\0';

            int tempNum = atoi(temp);
            int subNum = 0;
            if(tempNum >= 0)
            {
                char str[100];
                sprintf(str, "%d", tempNum);
                len = strlen(str);
                for(int i = 0; i < len; ++i)
                {
                    subNum = subNum*10 + (str[i] - '0');
                }

            }

            int diff = atoi(n) - subNum;
            char diffStr[100000];
            sprintf(diffStr,"%d", diff);
            strcpy(n,diffStr);
            len = strlen(n);
            continue;
        }




        int subNum = 0;
        char subNumStr[maxIndex+1];
        strncpy(subNumStr, n, maxIndex);
        subNumStr[maxIndex] = '\0';
        subNum = atoi(subNumStr);

        int diff = atoi(n) - subNum;
        char diffStr[100000];
        sprintf(diffStr,"%d", diff);
        strcpy(n,diffStr);
        len = strlen(n);

    }
    printf("%d\n", count);
    return 0;
}
