#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  long long max_yen = -1e18; 
  for (int i = 0; i < (1 << n); ++i) {
    vector<bool> smashed(n + 1, false);
    vector<int> smashed_nums;

    for (int j = 0; j < n; ++j) {
      if ((i >> j) & 1) {
        smashed_nums.push_back(j + 1);
      }
    }

    for (int num : smashed_nums) {
      for (int k = num; k <= n; k += num) {
        smashed[k] = true;
      }
    }

    long long current_yen = 0;
    for (int j = 1; j <= n; ++j) {
      if (!smashed[j]) {
        current_yen += a[j - 1];
      }
    }
    max_yen = max(max_yen, current_yen);
  }
  cout << max_yen << endl;
  return 0;
}
