#include <iostream>
#include <string>
#include <algorithm>

using namespace std;

bool isIncreasing(string s) {
  for (size_t i = 1; i < s.size(); ++i) {
    if (s[i] < s[i - 1]) return false;
  }
  return true;
}

int main() {
  string n;
  cin >> n;

  int count = 0;
  string current = "";
  for (char c : n) {
    if (current.empty()) {
      current += c;
    } else {
      if (c >= current.back()) {
        current += c;
      } else {
        count++;
        current = c;
      }
    }
  }
  count++;
  cout << count << endl;
  return 0;
}
