import java.math.BigInteger;
import java.util.Scanner;

public class Main {
    public static boolean isIncreasing(BigInteger n) {
        String s = n.toString();
        for (int i = 0; i < s.length() - 1; i++) {
            if (s.charAt(i) > s.charAt(i + 1)) {
                return false;
            }
        }
        return true;
    }

    public static int solve() {
        Scanner scanner = new Scanner(System.in);
        BigInteger n = new BigInteger(scanner.next());
        int count = 0;
        while (n.compareTo(BigInteger.ZERO) > 0) {
            BigInteger largestIncreasing = BigInteger.ZERO;
            String s = n.toString();
            for (int i = 0; i < s.length(); i++) {
                BigInteger current = new BigInteger(s.substring(0, i + 1));
                if (isIncreasing(current) && current.compareTo(n) <=0 ) {
                    if (current.compareTo(largestIncreasing) > 0) {
                        largestIncreasing = current;
                    }
                }
            }
            n = n.subtract(largestIncreasing);
            count++;
        }
        return count;
    }

    public static void main(String[] args) {
        System.out.println(solve());
    }
}
