    s = str(n)
    for i in range(len(s) - 1):
        if s[i] > s[i+1]:
            return False
    return True

def solve():
    n = int(input())
    count = 0
    while n > 0:
        s = str(n)
        i = 0
        while i < len(s) -1 and s[i] <= s[i+1]:
            i += 1

        if i == len(s) -1:
            count += 1
            n = 0
        else:
            num = int(s[:i+1])
            count += 1
            n -= num

    print(count)

