#include <stdio.h>
#include <stdlib.h>

int main() {
    long long N, K;
    scanf("%lld %lld", &N, &K);
    long long A[N], B[N];
    long long sum_A = 0;
    for (int i = 0; i < N; i++) {
        scanf("%lld %lld", &A[i], &B[i]);
        sum_A += A[i];
    }
    long long min_sum = -1;
    long long t0_N = 0, tN_0 = 0;
    long long max_single = 0;
    for (int i = 0; i < N; i++) {
        if (B[i] == 1) {
            max_single = max_single > A[i] ? max_single : A[i];
        }
    }
    if (max_single > K) {
        printf("-1\n");
        return 0;
    }
    t0_N = sum_A;
    tN_0 = sum_A;
    long long diff = 0;
    for (int i = 0; i < N; i++) {
        if (B[i] == 1) {
            diff += A[i];
        }
    }
    if (diff * 2 > K) {
        printf("-1\n");
        return 0;
    }

    long long ans = t0_N + tN_0;
    printf("%lld\n", ans);

    return 0;
}
