import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long k = sc.nextLong();
        long[] a = new long[n];
        int[] b = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
            b[i] = sc.nextInt();
        }
        long sumA = 0;
        for (long x : a) sumA += x;
        long minSum = Long.MAX_VALUE;
        for (long t0 = 0; t0 < k; t0++) {
            long tN = t0;
            long t0_rev = 0;
            long tN_rev = 0;
            boolean possible = true;
            for (int i = 0; i < n; i++) {
                tN += a[i];
                t0_rev += a[i];
            }
            long cur_t0 = t0;
            long cur_tN = tN;

            long cur_t0_rev = 0;
            long cur_tN_rev = 0;

            long[] arrival_0 = new long[n+1];
            long[] departure_0 = new long[n+1];
            long[] arrival_N = new long[n+1];
            long[] departure_N = new long[n+1];

            arrival_0[0] = t0;
            for(int i=0; i<n; ++i){
                departure_0[i] = arrival_0[i];
                arrival_0[i+1] = departure_0[i] + a[i];
            }

            arrival_N[n] = tN;
            for(int i=n-1; i>=0; --i){
                departure_N[i+1] = arrival_N[i+1];
                arrival_N[i] = departure_N[i+1] + a[i];
            }

            for (int i = 0; i < n; i++) {
                if (b[i] == 1 && Math.abs(arrival_0[i+1] - arrival_N[i]) < k) {
                    possible = false;
                    break;
                }
            }
            if (possible) {
                minSum = Math.min(minSum, cur_tN + cur_t0_rev);

            }
        }
        if (minSum == Long.MAX_VALUE) {
            System.out.println(-1);
        } else {
            System.out.println(minSum);
        }
    }
}
