#include <stdio.h>

#define MOD 1000000007

int main() {
    int N;
    scanf("%d", &N);
    long long x[N];
    for (int i = 0; i < N; i++) {
        scanf("%lld", &x[i]);
    }
    if (N == 1) {
        printf("1\n");
        return 0;
    }
    long long dp[N][N];
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < N; j++) {
            dp[i][j] = 0;
        }
    }
    for (int i = 0; i < N; i++) {
        dp[i][0] = 1;
    }
    for (int i = 1; i < N; i++) {
        for (int j = 1; j <= i; j++) {
            for (int k = 0; k < i; k++) {
                if (x[i] - x[k] >= j) {
                    dp[i][j] = (dp[i][j] + dp[k][j - 1]) % MOD;
                }
            }
        }
    }
    long long ans = 0;
    for (int i = 0; i < N; i++) {
        ans = (ans + dp[N - 1][i + 1]) % MOD;
    }
    printf("%lld\n", ans);
    return 0;
}

