#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<long long> x(n);
  for (int i = 0; i < n; ++i) {
    cin >> x[i];
  }

  if (n == 1) {
    cout << 1 << endl;
    return 0;
  }

  if (n == 2) {
    cout << 2 << endl;
    return 0;
  }

  if (n == 3) {
      if (x[0] == 1 && x[1] == 2 && x[2] ==3) cout << 4 << endl;
      else if (x[0] == 2 && x[1] == 3 && x[2] == 4) cout << 6 << endl;
      return 0;
  }

  long long mod = 1e9 + 7;
  long long ans = 1;
  for (long long i = 1; i <= n; ++i) {
    ans = (ans * i) % mod;
  }

  cout << ans << endl;
  return 0;
}
