#include <stdio.h>
#include <stdlib.h>

#define MAXN 50
#define MAXM 1225

int n, m;
int a[MAXM], b[MAXM];
int visited[MAXN + 1];
int low[MAXN + 1], dfn[MAXN + 1], timer;
int graph[MAXN + 1][MAXN + 1];
int bridge_count;

void dfs(int u, int p) {
    visited[u] = 1;
    low[u] = dfn[u] = ++timer;
    for (int v = 1; v <= n; v++) {
        if (graph[u][v]) {
            if (v == p) continue;
            if (visited[v]) {
                low[u] = (low[u] < dfn[v]) ? low[u] : dfn[v];
            } else {
                dfs(v, u);
                low[u] = (low[u] < low[v]) ? low[u] : low[v];
                if (low[v] > dfn[u]) {
                    bridge_count++;
                }
            }
        }
    }
}

int main() {
    scanf("%d %d", &n, &m);
    for (int i = 0; i < m; i++) {
        scanf("%d %d", &a[i], &b[i]);
        graph[a[i]][b[i]] = graph[b[i]][a[i]] = 1;
    }
    bridge_count = 0;
    timer = 0;
    dfs(1, 0);
    printf("%d\n", bridge_count);
    return 0;
}
