MOD = 10**9 + 7

def solve():
    n = int(input())
    x = list(map(int, input().split()))

    dp = {}

    def count_orders(current_state):
        if not current_state:
            return 1

        state_tuple = tuple(sorted(current_state))
        if state_tuple in dp:
            return dp[state_tuple]

        count = 0
        for i in range(len(current_state)):
            robot_pos = current_state[i]

            next_states = []
            if robot_pos -1 > 0 and robot_pos -1 not in current_state:
                next_states.append(current_state[:i] + [robot_pos - 1] + current_state[i+1:])

            if robot_pos - 2 > 0 and robot_pos - 2 not in current_state:
                next_states.append(current_state[:i] + [robot_pos - 2] + current_state[i+1:])

            for next_state in next_states:

                next_state_filtered = [pos for pos in next_state if pos > 0]
                count = (count + count_orders(next_state_filtered)) % MOD

        dp[state_tuple] = count
        return count

    print(count_orders(x))

solve()
