#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int main() {
    int n;
    scanf("%d", &n);
    char grid[n][n];
    for (int i = 0; i < n; i++) {
        scanf("%s", grid[i]);
    }
    int count = 0;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (grid[i][j] == '.')
                count++;
        }
    }
    if (count == 0) {
        printf("0\n");
        return 0;
    }
    int ans = 1e9;
    for (int i = 0; i < (1 << n); i++) {
        int cur = 0;
        char temp[n][n];
        memcpy(temp, grid, sizeof(grid));
        for (int j = 0; j < n; j++) {
            if ((i >> j) & 1) {
                cur++;
                char row[n];
                for (int k = 0; k < n; k++) {
                    row[k] = temp[j][k];
                }
                for (int k = 0; k < n; k++) {
                    temp[k][j] = row[k];
                }
            }
        }
        int flag = 1;
        for (int j = 0; j < n; j++) {
            for (int k = 0; k < n; k++) {
                if (temp[j][k] == '.') {
                    flag = 0;
                    break;
                }
            }
            if (!flag)
                break;
        }
        if (flag)
            ans = (ans > cur) ? cur : ans;
    }
    if (ans == 1e9)
        printf("-1\n");
    else
        printf("%d\n", ans);
    return 0;
}

