#include <iostream>
#include <vector>
#include <queue>
#include <map>

using namespace std;

int main() {
    int n;
    cin >> n;

    vector<string> grid(n);
    for (int i = 0; i < n; ++i) {
        cin >> grid[i];
    }

    map<vector<string>, int> dist;
    queue<vector<string>> q;

    dist[grid] = 0;
    q.push(grid);

    while (!q.empty()) {
        vector<string> current_grid = q.front();
        q.pop();

        bool all_black = true;
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < n; ++j) {
                if (current_grid[i][j] == '.') {
                    all_black = false;
                    break;
                }
            }
            if (!all_black) break;
        }

        if (all_black) {
            cout << dist[current_grid] << endl;
            return 0;
        }

        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < n; ++j) {
                vector<string> next_grid = current_grid;
                vector<char> row_colors;
                for (int k = 0; k < n; ++k) {
                    row_colors.push_back(current_grid[i][k]);
                }
                for (int k = 0; k < n; ++k) {
                    next_grid[k][j] = row_colors[k];
                }
                if (dist.find(next_grid) == dist.end()) {
                    dist[next_grid] = dist[current_grid] + 1;
                    q.push(next_grid);
                }
            }
        }
    }

    cout << -1 << endl;
    return 0;
}
