#include <stdio.h>
#include <stdlib.h>
#include <string.h>

char* process(char* t) {
    int n = strlen(t);
    int i;
    while (1) {
        i = -1;
        for (int j = 0; j < n - 1; j++) {
            if (t[j] == t[j + 1]) {
                i = j;
                break;
            }
        }
        if (i == -1) break;
        if (t[i] == 'z') {
            for (int j = i; j < n - 2; j++) {
                t[j] = t[j + 2];
            }
            n -= 2;
            t = (char*)realloc(t, n + 1);
            t[n] = '\0';
        } else {
            t[i] = t[i] + 1;
            for (int j = i + 1; j < n - 1; j++) {
                t[j] = t[j + 1];
            }
            n--;
            t = (char*)realloc(t, n + 1);
            t[n] = '\0';
        }
    }
    return t;
}

int main() {
    char s[500001];
    scanf("%s", s);
    int q;
    scanf("%d", &q);
    for (int i = 0; i < q; i++) {
        int l, r;
        scanf("%d %d", &l, &r);
        int len = r - l + 1;
        char* sub = (char*)malloc(len + 1);
        strncpy(sub, s + l - 1, len);
        sub[len] = '\0';
        char* res = process(sub);
        if (strlen(res) == 0) {
            printf("Yes\n");
        } else {
            printf("No\n");
        }
        free(res);
        free(sub);
    }
    return 0;
}
