#include <iostream>
#include <string>

using namespace std;

char process(string& t) {
  for (int i = 0; i < t.length() - 1; ++i) {
    if (t[i] == t[i + 1]) {
      if (t[i] == 'z') {
        t.erase(i, 2);
        i = -1;
      } else {
        t[i] = t[i] + 1;
        t.erase(i + 1, 1);
        i = -1;
      }
    }
  }
  return t.empty();
}

int main() {
  string s;
  cin >> s;
  int q;
  cin >> q;
  for (int i = 0; i < q; ++i) {
    int l, r;
    cin >> l >> r;
    string sub = s.substr(l - 1, r - l + 1);
    if (process(sub)) {
      cout << "Yes" << endl;
    } else {
      cout << "No" << endl;
    }
  }
  return 0;
}
