import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String s = sc.next();
        int Q = sc.nextInt();
        for (int i = 0; i < Q; i++) {
            int l = sc.nextInt() - 1;
            int r = sc.nextInt() - 1;
            String t = s.substring(l, r + 1);
            while (true) {
                int index = -1;
                for (int j = 0; j < t.length() - 1; j++) {
                    if (t.charAt(j) == t.charAt(j + 1)) {
                        index = j;
                        break;
                    }
                }
                if (index == -1) {
                    break;
                }
                if (t.charAt(index) == 'z') {
                    t = t.substring(0, index) + t.substring(index + 2);
                } else {
                    char c = (char) (t.charAt(index) + 1);
                    t = t.substring(0, index) + c + t.substring(index + 2);
                }
            }
            if (t.length() == 0) {
                System.out.println("Yes");
            } else {
                System.out.println("No");
            }
        }
    }
}
