def solve():
    s = input()
    q = int(input())

    def process(t):
        while True:
            i = -1
            for j in range(len(t) - 1):
                if t[j] == t[j+1]:
                    i = j
                    break
            if i == -1:
                break
            if t[i] == 'z':
                t = t[:i] + t[i+2:]
            else:
                t = t[:i] + chr(ord(t[i]) + 1) + t[i+2:]
        return t

    for _ in range(q):
        l, r = map(int, input().split())
        substring = s[l-1:r]
        result = process(substring)
        print("Yes" if len(result) == 0 else "No")

solve()
