#include <iostream>
#include <vector>

using namespace std;

int n, m;
vector<tuple<int, int, int>> edges;
vector<vector<pair<int, int>>> adj;
vector<bool> visited;
vector<int> tin, low;
int timer;
int bridges;

void dfs(int v, int p = -1) {
    visited[v] = true;
    tin[v] = low[v] = timer++;
    for (auto [to, edge_index] : adj[v]) {
        if (edge_index == p) continue;
        if (visited[to]) {
            low[v] = min(low[v], tin[to]);
        } else {
            dfs(to, edge_index);
            low[v] = min(low[v], low[to]);
            if (low[to] > tin[v]) {
                bridges++;
            }
        }
    }
}

int main() {
    cin >> n >> m;
    adj.resize(n + 1);
    visited.resize(n + 1, false);
    tin.resize(n + 1);
    low.resize(n + 1);
    bridges = 0;
    timer = 0;

    for (int i = 0; i < m; ++i) {
        int u, v;
        cin >> u >> v;
        edges.emplace_back(u, v, i);
        adj[u].emplace_back(v, i);
        adj[v].emplace_back(u, i);
    }

    dfs(1);
    cout << bridges << endl;
    return 0;
}
