import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        long mod = 1000000007;
        long ans = 1;
        for (long i = 1; i <= n; i++) {
            ans = (ans * i) % mod;
        }
        System.out.println(ans);
    }
}
