import java.util.*;

public class BridgeCount {
    static int n, m;
    static ArrayList<ArrayList<Integer>> graph;
    static int[] tin, low;
    static boolean[] visited;
    static int timer;
    static int bridgeCount;

    public static void dfs(int v, int p) {
        visited[v] = true;
        tin[v] = low[v] = timer++;
        for (int to : graph.get(v)) {
            if (to == p) continue;
            if (visited[to]) {
                low[v] = Math.min(low[v], tin[to]);
            } else {
                dfs(to, v);
                low[v] = Math.min(low[v], low[to]);
                if (low[to] > tin[v]) {
                    bridgeCount++;
                }
            }
        }
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        n = sc.nextInt();
        m = sc.nextInt();
        graph = new ArrayList<>();
        for (int i = 0; i <= n; i++) {
            graph.add(new ArrayList<>());
        }
        for (int i = 0; i < m; i++) {
            int u = sc.nextInt();
            int v = sc.nextInt();
            graph.get(u).add(v);
            graph.get(v).add(u);
        }
        tin = new int[n + 1];
        low = new int[n + 1];
        visited = new boolean[n + 1];
        timer = 0;
        bridgeCount = 0;
        dfs(1, -1);
        System.out.println(bridgeCount);
        sc.close();
    }
}
