#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int main() {
    int n;
    scanf("%d", &n);
    char s[n + 1];
    scanf("%s", s);
    char t[n + 1];
    for (int i = 0; i < (1 << n); i++) {
        for (int j = 0; j < n; j++) {
            if ((i >> j) & 1)
                t[j] = 'S';
            else
                t[j] = 'W';
        }
        int flag = 1;
        for (int j = 0; j < n; j++) {
            int prev = (j - 1 + n) % n;
            int next = (j + 1) % n;
            char ans;
            if (t[prev] == t[next])
                ans = 'o';
            else
                ans = 'x';
            if ((t[j] == 'S' && ans == 'x') || (t[j] == 'W' && ans == 'o')) {
                if (s[j] == 'o') {
                    flag = 0;
                    break;
                }
            } else if ((t[j] == 'S' && ans == 'o') || (t[j] == 'W' && ans == 'x')) {
                if (s[j] == 'x') {
                    flag = 0;
                    break;
                }
            }
        }
        if (flag) {
            for (int j = 0; j < n; j++)
                printf("%c", t[j]);
            printf("\n");
            return 0;
        }
    }
    printf("-1\n");
    return 0;
}
