import java.util.*;

public class Zoo {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        String s = sc.next();
        String ans = solve(n, s);
        System.out.println(ans);
    }

    static String solve(int n, String s) {
        for (int i = 0; i < (1 << n); i++) {
            StringBuilder sb = new StringBuilder();
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 0) sb.append('S');
                else sb.append('W');
            }
            boolean ok = true;
            for (int j = 0; j < n; j++) {
                char c1 = sb.charAt(j);
                char c2 = sb.charAt((j + 1) % n);
                char c3 = sb.charAt((j + n - 1) % n);
                boolean same = c2 == c3;
                char expected;
                if (c1 == 'S') expected = same ? 'o' : 'x';
                else expected = same ? 'x' : 'o';
                if (s.charAt(j) != expected) {
                    ok = false;
                    break;
                }
            }
            if (ok) return sb.toString();
        }
        return "-1";
    }
}
